import React from "react";
import './pages.css'
import { bookAddedToCart, bookRemovedToCart, allbookRemovedToCart} from "../../actions";
import { connect } from "react-redux";
const CartPage = ({ items, total, onIncrease, onDecrease, onDelete }) => {

    let totalPrice = items.reduce((accumulator, item) => {
        return accumulator + item.total;
    }, 0);

    const renderRow = (item, idx) => {
        const { id, title, count, total } = item;

        return (
            <tr key={id}>
                <td>{idx + 1}</td>
                <td>{title}</td>
                <td>{count}</td>
                <td>${total}</td>
                <td>
                    <button onClick={() => onDecrease(id)} className="btn btn-outline-warning btn-sm">
                        <i className="fa fa-minus-circle"/>
                    </button>
                    <button onClick={() => onIncrease(id)} className="btn btn-outline-success btn-sm ">
                        <i className="fa fa-plus-circle"/>
                    </button>
                    <button onClick={() => onDelete(id)} className="btn btn-outline-danger btn-sm ">
                        <i className="fa fa-trash-o"/>
                    </button>
                </td>
            </tr>
        );
    };
    return (

        <div className="shopping-cart-table">
            <h2>Your order</h2>
            <table className="table">
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Item</th>
                        <th>Count</th>
                        <th>Price</th>
                        <th>Action</th>
                    </tr>
                </thead>

                <tbody>
                {
                    items.map(renderRow)
                }
                </tbody>
            </table>
            <div className="total">
                    Total: ${totalPrice}
            </div>

        </div>
    );
};

const mapStateToProps = ({ shoppingCart: { cartItems, orderTotal} }) => {
    console.log(cartItems)
    return {
        items: cartItems,
        total: orderTotal
    }
};

const mapDispatchToProps = {
       onIncrease: bookAddedToCart,
       onDecrease: bookRemovedToCart,
       onDelete: allbookRemovedToCart

};


export default connect(mapStateToProps, mapDispatchToProps)(CartPage);
